INSERT INTO `posts` (`id`, `post_title`, `post_content`, `post_image`, `post_author`, `post_categories`, `post_type`, `post_status`, `post_visibility`, `post_comment_status`, `post_slug`, `post_tags`, `post_counter`, `created_at`, `updated_at`, `deleted_at`, `restored_at`, `created_by`, `updated_by`, `deleted_by`, `restored_by`, `is_deleted`) VALUES ('20', 'ALUMNI SMK BISA APA', '<h4 class=\"\">ALUMNI SMK BISA APA? | VIDEO TESTIMONI ALUMNI SMK NURUL JADID PAITON - PART 1</h4>\n<p><img src=\"http://localhost/smknj/media_library/posts/post-image-1612667621063.jpg\" alt=\"Lulusan SMK Bisa Apa? | Video Youtube\" width=\"759\" height=\"377\" /></p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; Alhamdulillah, kami ikut dan merasa bangga bahwa Alumni SMK Nurul Jadid Paiton Probolinggo sebagai salah satu SMK berbasis Pesantren mampu bersaing dan berkiprah di berbagai bidang sesuai passion dan kompetensi masing-masing di tengah Masyarakat.</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; Semoga menginspirasi semua untuk mengejar mimpi-mimpi dan cita-cita masing-masing di berbagai bidang untuk menyebarkan manfaat juga mengabdi setulus hati kepada sesama Pasca Pendidikan di Pesantren.</p>\n<p><a title=\"ALUMNI SMK BISA APA? | VIDEO TESTIMONI ALUMNI SMK NURUL JADID PAITON - PART 1\" href=\"https://youtu.be/KKq0BtLbgAA\" target=\"_blank\" rel=\"noopener\">https://youtu.be/KKq0BtLbgAA</a></p>', '3ebd875d67511779bef35f681d48e308.jpg', '1', '8', 'post', 'publish', 'public', 'open', 'alumni-smk-bisa-apa', '', '11', '2021-02-07 10:19:28', '2021-02-11 11:56:43', NULL, NULL, '1', '1', NULL, NULL, 'false');
INSERT INTO `posts` (`id`, `post_title`, `post_content`, `post_image`, `post_author`, `post_categories`, `post_type`, `post_status`, `post_visibility`, `post_comment_status`, `post_slug`, `post_tags`, `post_counter`, `created_at`, `updated_at`, `deleted_at`, `restored_at`, `created_by`, `updated_by`, `deleted_by`, `restored_by`, `is_deleted`) VALUES ('21', 'APLIKASI MANASIK HAJI SMKNJ 2020', '<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://localhost/smknj/media_library/posts/post-image-1612668587515.jpg\" alt=\"Game Manasik Haji\" width=\"570\" height=\"388\" /></p>\n<p style=\"text-align: center;\">APLIKASI ASIK-JI</p>\n<p>Manasik Haji berbasis Virtual Reality (VR)</p>\n<p>&nbsp;</p>\n<p>Aplikasi ASIK-JI VR ini merupakan Karya Peserta Didik SMK Nurul Jadid untuk memudahkan Calon Jemaah haji dalam mempersiapkan dirinya memahami Tata Cara Manasik Haji dan Umroh sesuai dengan tuntunan atau Pedoman Kemenag RI.</p>\n<p>Aplikasi ASIK-JI dilengkapi dengan Audio Bacaan, Visualisasi Lokasi, Grafis tulisan bacaan sehingga mempermudah dalam memahami panduan Ibadah Manasik Haji.</p>\n<p>Aplikasi tutorial atau panduan Ibadah Manasik Haji berbasis Virtual Reality (VR) merupakan karya peserta didik SMK Nurul Jadid sebagai karya inovatif untuk mempermudah para calon jamaah haji belajar tata cara Manasik Haji sesuai panduan Standard Kementrian Agama Republik Indonesia.<br /><br />Aplikasi Manasik Haji (ASIKJI) ini dilengkapi dengan penjelasan tata cara ibadah dan audio bacaan dalam melaksanakan Ibadah Manasik Haji.</p>\n<p>Aplikasi Manasik Haji VR Karya Peserta Didik SMK Nurul Jadid Paiton Probolinggo dapat diakses di Google Play.</p>\n<p>&nbsp;</p>\n<p>SMK Nurul Jadid</p>\n<p>Berinovasi Tiada Henti</p>\n<p>Mengabdi Setulus Hati</p>\n<p><a href=\"https://www.facebook.com/photo/?fbid=1529475780545218&amp;set=a.167753736717436\" target=\"_blank\" rel=\"noopener\">https://www.facebook.com/photo/?fbid=1529475780545218&amp;set=a.167753736717436</a></p>', '6d7e602cbff0274d9f49da0ffd15e689.jpg', '1', '1,5,23', 'post', 'publish', 'public', 'open', 'aplikasi-manasik-haji-smknj-2020', '', '9', '2021-02-07 10:34:12', '2021-02-15 08:38:46', NULL, NULL, '1', '1', NULL, NULL, 'false');
