INSERT INTO `posts` (`id`, `post_title`, `post_content`, `post_image`, `post_author`, `post_categories`, `post_type`, `post_status`, `post_visibility`, `post_comment_status`, `post_slug`, `post_tags`, `post_counter`, `created_at`, `updated_at`, `deleted_at`, `restored_at`, `created_by`, `updated_by`, `deleted_by`, `restored_by`, `is_deleted`) VALUES ('25', 'SMK Nurul Jadid Raih penghargaan dari SEAMOLEC ditengah Pandemi Covid-19', '<p style=\"text-align: center;\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://localhost/smknj/media_library/posts/post-image-1612675064778.jpg\" alt=\"\" width=\"731\" height=\"337\" />Penyerahan Piagam Penghargaan Kepada Guru SMK Nurul Jadid (Foto: Rahmat)</p>\n<p><strong>Bogor -&nbsp;</strong>Pemanfaatan teknologi informasi dan komunikasi (TIK) merupakan salah satu hal yang sangat penting dalam proses pembelajaran maupun sosialisasi, apalagi dalam situasi pandemi Covid-19 saat ini, yang mana proses pembelajaran dilakukan secara daring serentak di seluruh Indonesia. Hal tersebut, mendorong SMK Nurul Jadid Kabupaten Probolinggo untuk terus mengoptimalkan dan meningkatkan kempetensi sumber daya manusia (SDM) dalam pengembangan media komunikasi, informasi dan edukasi/pembelajaran.</p>\n<p>Kemarin (selasa 07/12/2020) Dua guru perwakilan SMK Nurul Jadid Bapak Rahmat Hidayat dan Bapak Hairul Umam menghadiri pertemuan bersama SEAMOLEC di Bogor dalam acara Pemberian Penghargaan yang bertempat di Grand Savero Hotel Bogor, Selepas acara selesai SMK Nurul jadid memperoleh sebuah apresiasi award dari SEAMOLEC sebagai pengakuan atas penerapan model program pembelajaran jarak jauh yang merupakan sebuah program SEAMOLEC.</p>\n<p>Diketahui, SEAMEO Regional Open Learning Centre (SEAMOLEC) adalah salah satu dari 26 pusat pelatihan dan pengembangan pendidikan jarak jauh yang berada di bawah naungan Southeast Asian Ministers of Education Organization (<a href=\"https://seamolec.org/\" target=\"_blank\" rel=\"noopener\">SEAMEO</a>) atau Organisasi Menteri-menteri Pendidikan se Asia Tenggara yang bertanggung jawab untuk mengembangkan Pendidikan Terbuka dan Pendidikan Jarak Jauh (PTJJ) di Asia Tenggara.</p>\n<p>Sebelumnya, selasa (8/11/2019) Kepala SMK Nurul Jadid Bapak Abdul Manaf Firdaus menghadiri acara &ldquo;Workshop Program Kemitraan dan Diseminasi Praktik-praktik Baik Program PTJJ SEAMOLEC di Indonesia&rdquo; Sekaligus melakukan kesepakatan dalam bentuk kerjasama atau MOU Di Swiss-Belhotel Pondok Indah Jakarta.</p>\n<p>&ldquo;Pengembangan pendidikan akan semakin efektif dengan pemanfaatan Teknologi Informasi dan Komunikasi (TIK) dalam proses pembelajaran, dan bukan tidak mungkin lagi 5 atau 10 tahun yang akan daring Pembelajaran Jarak Jauh (PJJ) ini akan sangat diminati. Kami sangat bersyukur bisa mendapatkan penghargai ini. Semoga kerja sama yang sudah disepakati memberikan dampak yang baik bagi SMK Nurul Jadid.&ldquo; Ujar Rahmat.</p>', 'd5be68f3297d40441d0cdafc6ec7774b.jpg', '1', '1,7', 'post', 'publish', 'public', 'open', 'smk-nurul-jadid-raih-penghargaan-dari-seamolec-ditengah-pandemi-covid-19', '', '7', '2021-02-07 12:22:52', '2021-02-11 11:53:39', NULL, NULL, '1', '1', NULL, NULL, 'false');
INSERT INTO `posts` (`id`, `post_title`, `post_content`, `post_image`, `post_author`, `post_categories`, `post_type`, `post_status`, `post_visibility`, `post_comment_status`, `post_slug`, `post_tags`, `post_counter`, `created_at`, `updated_at`, `deleted_at`, `restored_at`, `created_by`, `updated_by`, `deleted_by`, `restored_by`, `is_deleted`) VALUES ('26', 'Rekayasa Perangkat Lunak', '<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://localhost/smknj/media_library/posts/post-image-1612329811883.png\" alt=\"RPL\" width=\"328\" height=\"305\" /></p>\n<p>Menghasilkan tamatan yang memiliki pengetahuan, keterampilan dan nilai serta sikap yang terintegrasi dan kecakapan kerja dalam bidang pemograman database, pemograman WEB, pemograman berorientasi obyek dan antarmuka yang diterapkan atas dasar kewiraswastaan serta mampu mengadaptasi perkembangan masyarakat yang sesuai dengan kemajuan ilmu dan teknologi serta dapat memenuhi tuntutan dunia kerja masa sekarang dan masa yang akan datang.</p>\n<p>&nbsp;</p>\n<ul>\n<li>Algoritma pemograman</li>\n<li>Pembuatan website statis dan dinamis</li>\n<li>Aplikasi VB dan VB.Net</li>\n<li>Bahasa pemograman data deskripsi (SQL)</li>\n<li>Bahasa pemograman berorientasi objek (Java/C++)</li>\n<li>Integrasi basis data situs web</li>\n<li>Integrasi basis data dengan MS SQL server</li>\n<li>Integrasi basis data dengan PL/SQL</li>\n</ul>', '9600e1bc416c73aec91100351d77d91a.jpg', '1', '6', 'post', 'publish', 'public', 'close', 'rekayasa-perangkat-lunak', '', '17', '2021-02-10 09:53:58', '2021-02-15 08:35:37', NULL, NULL, '1', '1', NULL, NULL, 'false');
INSERT INTO `posts` (`id`, `post_title`, `post_content`, `post_image`, `post_author`, `post_categories`, `post_type`, `post_status`, `post_visibility`, `post_comment_status`, `post_slug`, `post_tags`, `post_counter`, `created_at`, `updated_at`, `deleted_at`, `restored_at`, `created_by`, `updated_by`, `deleted_by`, `restored_by`, `is_deleted`) VALUES ('27', 'Teknik Komputer Dan Jaringan', '<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://localhost/smknj/media_library/posts/post-image-1612405851008.jpg\" alt=\"\" width=\"225\" height=\"233\" /></p>\n<p><strong>Program Studi Keahlian Teknik Komputer dan Jaringan </strong></p>\n<p>Menghasilkan tamatan yang memiliki pengetahuan, keterampilan dan nilai serta sikap yang terintegrasi dan kecakapan kerja dalam bidang Teknik Komputer dan Jaringan dengan&nbsp; menerapkan&nbsp; kewiraswastaan&nbsp; serta&nbsp; mampu&nbsp; mengadaptasi&nbsp; perkembangan masyarakat yang sesuai dengan kemajuan ilmu dan teknologi serta dapat memenuhi tuntutan dunia kerja masa sekarang dan masa yang akan datang.</p>\n<ul>\n<li>Menginstalasi Personal Computer (PC)</li>\n<li>Menginstalasi Sistem Operasi berbasis Text dan GUI</li>\n<li>Mengintalasi Software</li>\n<li>Mendiagnosis Permasalahan dan Memperbaiki PC dan Periferal</li>\n<li>Melakukan Perawatan PC dan Periferal</li>\n<li>Membackup dan Merestore Software</li>\n<li>Mengintalasi Perangkat Jaringan Lokal (Local Areal Network)</li>\n<li>Mendiagnosis Permasalahan dan Memperbaiki PC yang tersambung pada jaringan</li>\n<li>Menginstalasi OS berbasis Text dan GUI yang tersambung pada jaringan</li>\n<li>Mengintalasi perangkat jaringan berbasis luas (Wide Area Network)</li>\n<li>Mendiagnosis Permasalahan dan Memperbaiki Perangkat yang tersambung pada jaringan berbasis luas (WAN)</li>\n<li>Mengadministrasi server dalam jaringan.</li>\n</ul>', '20deabd6dcdd7b420813b17ca69a2241.jpg', '1', '6', 'post', 'publish', 'public', 'close', 'teknik-komputer-dan-jaringan', '', '4', '2021-02-10 09:57:52', '2021-02-15 08:35:59', NULL, NULL, '1', '1', NULL, NULL, 'false');
INSERT INTO `posts` (`id`, `post_title`, `post_content`, `post_image`, `post_author`, `post_categories`, `post_type`, `post_status`, `post_visibility`, `post_comment_status`, `post_slug`, `post_tags`, `post_counter`, `created_at`, `updated_at`, `deleted_at`, `restored_at`, `created_by`, `updated_by`, `deleted_by`, `restored_by`, `is_deleted`) VALUES ('28', 'Multimedia', '<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://localhost/smknj/media_library/posts/post-image-1612403978607.png\" alt=\"\" width=\"141\" height=\"209\" /><strong>Program Studi Keahlian Multimedia</strong></p>\n<p>Menyiapkan tenaga pelaksana&nbsp; di&nbsp; bidang&nbsp; pengembangan aplikasi multimedia interaktif yang berkompeten dalam pencitraan publik dengan menggunakan animasi digital, web interaktif, merekam dan menyunting audio video serta yang&nbsp; memiliki pengetahuan, keterampilan, nilai dan sikap sebagai manusia yang bertanggung jawab dan mencintai profesi pekerjaannya.</p>\n<p>&nbsp;</p>\n<ul>\n<li>Dasar desain grafis Multimedia</li>\n<li>Fotografi digital</li>\n<li>Desain dan pengembangan Web</li>\n<li>Produksi video</li>\n<li>Perancangan animasi</li>\n<li>Integrasi 2D ke dalam sajian Multimedia</li>\n<li>Integrasi fotografi digital ke dalam sajian multimedia</li>\n<li>Integrasi audio kedalam sajian multimedia</li>\n<li>Menggunakan peralatan kantor</li>\n<li>Melaksanakan Prosedur Administrasi</li>\n</ul>\n<p>Mengaplikasikan keterampilan dan komunikasi berbasis Operasi Office</p>', 'c1fee3e9a0a835a44c191f2792fe99b7.jpg', '1', '6', 'post', 'publish', 'public', 'close', 'multimedia', '', '8', '2021-02-10 12:12:05', '2021-02-15 08:36:19', NULL, NULL, '1', '1', NULL, NULL, 'false');
