INSERT INTO `posts` (`id`, `post_title`, `post_content`, `post_image`, `post_author`, `post_categories`, `post_type`, `post_status`, `post_visibility`, `post_comment_status`, `post_slug`, `post_tags`, `post_counter`, `created_at`, `updated_at`, `deleted_at`, `restored_at`, `created_by`, `updated_by`, `deleted_by`, `restored_by`, `is_deleted`) VALUES ('30', 'Tata Busana', '<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://localhost/smknj/media_library/posts/post-image-1612408598204.jpg\" alt=\"\" width=\"226\" height=\"225\" /></p>\n<p>&nbsp; &nbsp; &nbsp; Tata busana merupakan suatu disiplin ilmu dan seni mengenai penerapan desain, estetika, dan keindahan alami untuk pakaian dan hiasan tambahannya. Penerapan tata busana dipengaruhi oleh corak budaya dan sosial, yang beragam tergantung pada waktu dan tempat.</p>', '70fb407da2391e5deb18ca055b69e10e.jpg', '1', '6', 'post', 'publish', 'public', 'close', 'tata-busana', '', '6', '2021-02-10 12:16:46', '2021-02-11 11:50:18', NULL, NULL, '1', '1', NULL, NULL, 'false');
INSERT INTO `posts` (`id`, `post_title`, `post_content`, `post_image`, `post_author`, `post_categories`, `post_type`, `post_status`, `post_visibility`, `post_comment_status`, `post_slug`, `post_tags`, `post_counter`, `created_at`, `updated_at`, `deleted_at`, `restored_at`, `created_by`, `updated_by`, `deleted_by`, `restored_by`, `is_deleted`) VALUES ('31', 'Agribisnis Pengolahan Hasil Perikanan', '<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://localhost/smknj/media_library/posts/post-image-1612406705409.jpg\" alt=\"\" width=\"267\" height=\"189\" /></p>\n<p>Salah satu kompetensi keahlian yang dibuka adalah Agribisnis Pengolahan Hasil Perikanan (APHPi). Jurusan ini mempelajari bagaimana mengolah hasil perikanan mulai dari pasca panen hingga menghasilkan produk perikanan berkualitas tinggi yang zero waste dan ramah lingkungan. Selain diajarkan bagaimana untuk mengolah produk perikanan dengan kualitas terbaik, siswa juga diajarkan bagaimana cara menjual atau memasaran produk yang mereka buat. Dengan kegiatan tersebut diharapkan siswa dapat berwirausaha secara mandiri.</p>\n<p>&nbsp;</p>\n<ul>\n<li><strong>Tujuan Jurusan APHPi</strong></li>\n</ul>\n<p>Adapun tujuan dari didirikannya jurusan ini yaitu :</p>\n<ol>\n<li>Meningkatkan keimanan dan ketaqwaan siswa kepada Tuhan YME.</li>\n<li>Meningkatkan kompetensi siswa dalam bidang pengolahan hasil perikanan.</li>\n<li>Mengembangkan potensi siswa agar memiliki kepedulian terhadap lingkungan hidup, dengan secara aktif turut memelihara dan melestarikan lingkungan hidup, serta memanfaatkan sumber daya alam dengan efektif dan efisien.</li>\n<li>Meningkatkan pemanfaatan sumber daya perikanan kelautan yang melimpah di wilayah Trenggalek, khususnya Kecamatan Panggul</li>\n<li>Melakukan pekerjaan sebagai pengolah hasil perikanan secara mandiri atau wirausaha</li>\n<li>Melakukan pekerjaan sebagai pelaksana penanganan dan pengolahan serta pengujian mutu hasil perikanan yang profesional dalam indusstri pengolahan hasil perikanan.</li>\n</ol>', '9a1a6785defeee8da4746fd0cc20b882.jpg', '1', '6', 'post', 'publish', 'public', 'close', 'agribisnis-pengolahan-hasil-perikanan', '', '6', '2021-02-10 12:35:03', '2021-02-15 08:36:45', NULL, NULL, '1', '1', NULL, NULL, 'false');
INSERT INTO `posts` (`id`, `post_title`, `post_content`, `post_image`, `post_author`, `post_categories`, `post_type`, `post_status`, `post_visibility`, `post_comment_status`, `post_slug`, `post_tags`, `post_counter`, `created_at`, `updated_at`, `deleted_at`, `restored_at`, `created_by`, `updated_by`, `deleted_by`, `restored_by`, `is_deleted`) VALUES ('32', 'Teknik Pembangkit Tenaga Listrik', '<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://localhost/smknj/media_library/posts/post-image-1612406579920.png\" alt=\"\" width=\"226\" height=\"225\" /></p>\n<p>&nbsp;</p>\n<p><strong>Teknik Pembangkit Tenaga Listrik</strong>&nbsp;atau&nbsp;<strong>Teknik Pembangkit Listrik</strong>&nbsp;disingkat&nbsp;<strong>TPTL</strong>&nbsp;adalah sebuah cabang dari bidang&nbsp;<a title=\"Teknik energi\" href=\"https://id.wikipedia.org/wiki/Teknik_energi\">Teknik energi</a>, dan didefinisikan sebagai rekayasa dan teknologi yang dibutuhkan untuk produksi dari stasiun pusat tenaga listrik.<sup id=\"cite_ref-:02_1-0\" class=\"reference\"></sup>&nbsp;<a title=\"Teknik\" href=\"https://id.wikipedia.org/wiki/Teknik\">Teknik</a>&nbsp;ini difokuskan pada&nbsp;<a title=\"Pembangkit listrik\" href=\"https://id.wikipedia.org/wiki/Pembangkit_listrik\">pembangkit listrik</a>&nbsp;untuk industri dan komunitas, bukan hanya untuk produksi listrik rumah tangga. Bidang ini adalah bidang disiplin menggunakan dasar teori&nbsp;<a title=\"Teknik mesin\" href=\"https://id.wikipedia.org/wiki/Teknik_mesin\">teknik mesin</a>&nbsp;dan&nbsp;<a title=\"Teknik listrik\" href=\"https://id.wikipedia.org/wiki/Teknik_listrik\">listrik</a>. Aspek teknik manajemen&nbsp;<a title=\"Pembangkit listrik\" href=\"https://id.wikipedia.org/wiki/Pembangkit_listrik\">pembangkit listrik</a>&nbsp;telah berkembang dengan&nbsp;<a title=\"Teknologi\" href=\"https://id.wikipedia.org/wiki/Teknologi\">teknologi</a>&nbsp;dan menjadi semakin rumit. Pengenalan teknologi&nbsp;<a class=\"mw-disambig\" title=\"Nuklir\" href=\"https://id.wikipedia.org/wiki/Nuklir\">nuklir</a>&nbsp;dan kemajuan&nbsp;<a title=\"Teknologi\" href=\"https://id.wikipedia.org/wiki/Teknologi\">teknologi</a>&nbsp;lain yang ada telah memungkinkan daya diciptakan dengan lebih banyak cara dan dalam skala yang lebih besar daripada yang mungkin sebelumnya. Penugasan berbagai jenis&nbsp;<a class=\"mw-redirect\" title=\"Insinyur\" href=\"https://id.wikipedia.org/wiki/Insinyur\">insinyur</a>&nbsp;untuk desain, konstruksi, dan pengoperasian&nbsp;<a title=\"Pembangkit listrik\" href=\"https://id.wikipedia.org/wiki/Pembangkit_listrik\">pembangkit listrik</a>&nbsp;baru bergantung pada jenis sistem yang sedang dibangun, seperti apakah itu&nbsp;<a title=\"Pembangkit listrik\" href=\"https://id.wikipedia.org/wiki/Pembangkit_listrik\">pembangkit listrik</a>&nbsp;berbahan bakar&nbsp;<a title=\"Fosil\" href=\"https://id.wikipedia.org/wiki/Fosil\">fosil</a>,&nbsp;<a class=\"mw-redirect\" title=\"PLTN\" href=\"https://id.wikipedia.org/wiki/PLTN\">pembangkit listrik tenaga nuklir</a>,&nbsp;<a class=\"mw-redirect\" title=\"PLTA\" href=\"https://id.wikipedia.org/wiki/PLTA\">pembangkit listrik tenaga air</a>, dan&nbsp;<a class=\"mw-redirect\" title=\"PLTS\" href=\"https://id.wikipedia.org/wiki/PLTS\">pembangkit listrik tenaga surya.</a></p>', '65d22b4d7f0e4022fbd1ba3a4849519b.jpg', '1', '6', 'post', 'publish', 'public', 'close', 'teknik-pembangkit-tenaga-listrik', '', '8', '2021-02-10 12:52:12', '2021-02-11 12:03:40', NULL, NULL, '1', '1', NULL, NULL, 'false');
