INSERT INTO `posts` (`id`, `post_title`, `post_content`, `post_image`, `post_author`, `post_categories`, `post_type`, `post_status`, `post_visibility`, `post_comment_status`, `post_slug`, `post_tags`, `post_counter`, `created_at`, `updated_at`, `deleted_at`, `restored_at`, `created_by`, `updated_by`, `deleted_by`, `restored_by`, `is_deleted`) VALUES ('34', 'Rundown Prakerin 2021 Kelas IX', '<p style=\"text-align: right;\"><img style=\"float: left;\" src=\"http://localhost/smknj/media_library/posts/post-image-1613020623606.jpg\" alt=\"Prekerin\" width=\"364\" height=\"364\" />&nbsp;<img src=\"http://localhost/smknj/media_library/posts/post-image-1613020675156.jpg\" alt=\"\" width=\"362\" height=\"360\" /></p>\n<div id=\"js_2\" class=\"_5pbx userContent _3576\" data-testid=\"post_message\" data-ft=\"{&quot;tn&quot;:&quot;K&quot;}\">\n<div id=\"id_6024bd423d1fc5b59731108\" class=\"text_exposed_root text_exposed\">\n<p>&nbsp;</p>\n<p>Rundown Acara Prakerin Tahun 2021<br />1. Rabu, 10 februari 2021 Pembekalan kompetensi kejuruan<br />2. Rabu,10 februari 2021 Penguatan Karakter<br />3. Kamis, 11 februari 2021 materi kedisiplinan<br />4. Kamis, 11 februari 2021materi terkait dunia industri oleh humas pesantren<span class=\"text_exposed_show\"><br />5. Kamis, 11 februari 2021materi terkait pembekalan prakerin<br />6. Rabu, 17 februari, pelepasan peserta Prakerin di lingkungan pondok pesantren nurul jadid</span></p>\n</div>\n</div>\n<div class=\"_3x-2\" data-ft=\"{&quot;tn&quot;:&quot;H&quot;}\">&nbsp;</div>', '65480d393356cb5f431524f5f6d3cc25.jpg', '1', '1,23', 'post', 'publish', 'public', 'open', 'rundown-prakerin-2021-kelas-ix', 'prakerin 2021', '25', '2021-02-11 12:26:36', NULL, NULL, NULL, '1', NULL, NULL, NULL, 'false');
INSERT INTO `posts` (`id`, `post_title`, `post_content`, `post_image`, `post_author`, `post_categories`, `post_type`, `post_status`, `post_visibility`, `post_comment_status`, `post_slug`, `post_tags`, `post_counter`, `created_at`, `updated_at`, `deleted_at`, `restored_at`, `created_by`, `updated_by`, `deleted_by`, `restored_by`, `is_deleted`) VALUES ('35', 'SMKNJ Memberikan Apresiasi Kepada Siswa Berprestasi', '<p>&nbsp;</p>\n<p><img style=\"float: left;\" src=\"http://localhost/smknj/media_library/posts/post-image-1613021529460.jpg\" alt=\"\" width=\"364\" height=\"273\" /></p>\n<p><img style=\"float: right;\" src=\"http://localhost/smknj/media_library/posts/post-image-1613021576777.jpg\" alt=\"\" width=\"363\" height=\"273\" /></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://localhost/smknj/media_library/posts/post-image-1613021635791.jpg\" alt=\"\" width=\"487\" height=\"366\" />Paiton - 11 Januari 2020 dihelat Acara Penganugerahan dan Penobatan Bintang Pelajar, Kompetensi Keahlian, Keagamaan dan Kelas SMK Nurul Jadid Paiton Probolinggo Putra yang dihadiri oleh Kepala Sekolah, Bapak Abdul Manaf Firdaus, M.Pd. yang bertepatan hari Senin pagi di gedung sekolah.</p>\n<div id=\"js_w\" class=\"_5pbx userContent _3576\" data-testid=\"post_message\" data-ft=\"{\">\n<div id=\"id_6024c03a40c5e3756922700\" class=\"text_exposed_root text_exposed\">\n<p><br />Pembacaan SK Bintang dibawakan langsung oleh Waka. Kurikulum, Bapak Moh. Arief Hariyanto, S.Pd. nampak juga Bapak Abdul&nbsp;<span class=\"text_exposed_show\">Hadi, M.Pd.I. selaku Waka. Humas dan Bapak Danil Abdillah, S.Pd. selaku Penanggung Jawab Program PJB Academy menyerahkan Piala dan Piagam Penghargaan kepada peserta didik berprestasi Semester Ganjil tahun 2021.</span></p>\n<p><span class=\"text_exposed_show\"><br />Seluruh Guru dan Tenaga Kependidikan lainnya ikut mendampingi dan mengawal suksesnya kegiatan ini khususnya Pembina OSIS Bapak Aulia Akbar Maulana, S.Kom dan Waka. Kesiswaan Bapak Nurul Iman, M.Pd.I. harapan dari kegiatan ini dapat memacu dan memicu semangat belajar serta berprestasi siswa-siswi SMK Nurul Jadid Paiton Probolinggo ke depan.</span></p>\n</div>\n</div>\n<div class=\"_3x-2\" data-ft=\"{\">&nbsp;</div>', '006f2b601e26ae9e5a7a071ad693bba7.jpg', '1', '1', 'post', 'publish', 'public', 'open', 'smknj-memberikan-apresiasi-kepada-siswa-berprestasi', 'smknj, smk, smknuruljadidpaiton, penobatanbintang, santriberprestasi', '8', '2021-02-11 12:36:17', '2021-02-15 08:28:03', NULL, NULL, '1', '1', NULL, NULL, 'false');
